import Vue from "vue";
import Vuex from 'vuex';
// 需要使用插件一次
Vue.use(Vuex);
// state仓库存储数据的地方
const state = {
    count:1,
};
// 修改state的唯一手段
const mutations = {
    ADD(state) {
        state.count++;

    },
    MINUS(count) {
        conut--
    }
};
// 处理action,可以书写自己的业务逻辑，也可以处理异步
const actions = {
    // 这里可以书写业务逻辑，但是不能修改state
    add({commit}) {
        commit('ADD');

    },
    minus({commit}) {
        commit('MINUS');
    }
};
// getters理解为计算属性，用于简化仓库数据，让组件获取数据更加方便
const getters = {};




// 对外暴露一个store类的实例，store是vuex中的一个构造函数
export default new Vuex.Store(
    {state,
    mutations,
    actions,
    getters
}
);
