// 配置路由
import Vue from 'vue'
import VueRouter from 'vue-router'

// 使用插件
Vue.use(VueRouter);
// 引入路由组件
import MyHome from '@/pages/Home/MyHome.vue'
import MySearch from '@/pages/Search/MySearch.vue'
import MyLogin from '@/pages/Login'
import MyRegister from '@/pages/Register'
// 配置路由，暴露实例
export default new VueRouter({
    routes: [
        {
            path:'/home',
            component:MyHome
        },
        {
            path:'/search',
            component:MySearch
        },
        {
            path:'/login',
            component:MyLogin
        },
        {
            path:'/register',
            component:MyRegister
        },
        // 重定向
        {
            path:'*',
            redirect:'/home'
        }

    ]
})
