// 对于axios进行二次封装
import axios from "axios";
// 利用axios对象的方法create，去创建一个axios实例
const request = axios.create({
    // 设置默认路径
    baseURL:'/api',
    // 设置timeout
    timeout:5000,
})
// 请求拦截器
request.interceptors.request.use(
    (config)=> {
        // 包含一个请求头headers
        return config;

    }

);
// 响应拦截器
request.interceptors.response.use((res)=> {
    return res.data;
},(error)=> {
    console.log(error)
});


// 对外暴露
export default request;
